import './App.css';
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import EmployeeForm from './components/EmployeeForm';
import Modal from 'react-modal'

function App() {
  const [employees, setEmployees] = useState({ })
  const [modalIsOpen, setIsOpen] = useState(false)

  function openModal() {
    setIsOpen(true)
  }

  useEffect(() => {
    axios.get('http://localhost:5000/employees')
      .then(response => { setEmployees(response) })
      .catch(error => { console.log(error) })
  }, [employees])

  const getEmployeeList = (employees) => {
    const employeeList = employees.data.map(employee =>
      <li className="employee-item" key={employee.id}>
        <ul className="employee-name">
          {employee.first_name} {employee.last_name}
        </ul>
        <ul className="employee-contact-info">
          <div className="email">Email: {employee.email}</div>
          <div className="phone">Phone: {employee.phone}</div>
        </ul>
        <ul className="employee-details">
          <div className="position">Position: {employee.position} </div>
          <div className="salary">Salary: ${employee.salary}</div>
        </ul>
        <div className="edit-delete">
          <button className="edit" onClick="patchEmployee">Edit</button>
          <button className="delete">Delete</button>
        </div>
      </li>
    )
    return employeeList
  }

  return (
    <>
      <div className="container">
        {employees.status === 200 ?
          <div className="employee-list-wrapper">
            {getEmployeeList(employees)}
          </div>
          :
          <h3>LOADING</h3>
        }
        <EmployeeForm />
      </div>
    </>
  );
}

export default App;
