import './App.css';
import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import EmployeeForm from './components/EmployeeForm';
import EditButton from './components/EditButton';

function App() {
  const [employees, setEmployees] = useState({ })

  useEffect(() => {
    axios.get('http://localhost:5000/employees')
      .then(response => { setEmployees(response) })
      .catch(error => { console.log(error) })
  }, [])

  const handleUpdate = (employee, index) => {
    return (event) => {
      const value = event.target.value
      setEmployees(prevEmployees => ({ ...prevEmployees, [index]: { ...employee, value } }))
      console.log(employees)
    }
  }

  const getEmployeeList = (employees) => {
    const employeeList = employees.data.map((employee, index) =>
      <li className="employee-item" key={employee.email}>
        <ul className="employee-name">
          {employee.first_name} {employee.last_name}
        </ul>
        <ul className="employee-contact-info">
          <div className="email">Email: {employee.email}</div>
          <div className="phone">Phone: {employee.phone}</div>
        </ul>
        <ul className="employee-details">
          <div className="position">Position: {employee.position} </div>
          <div className="salary">Salary: ${employee.salary}</div>
        </ul>
        <div className="edit-delete">
          <EditButton employee={employee} index={index} updateParentState={handleUpdate}/>
          <button className="delete">Delete</button>
        </div>
      </li>
    )
    return employeeList
  }

  return (
    <>
      {console.log(employees)}
      <div className="container">
        {employees.status === 200 ?
          <div className="employee-list-wrapper">
            {getEmployeeList(employees)}
          </div>
          :
          <h3>LOADING</h3>
        }
        <EmployeeForm />
      </div>
    </>
  );
}

export default App;
